import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  AppRegistry,
  SafeAreaView,
  TouchableOpacity,
} from "react-native";

import { NativeRouter, Route, Link, Redirect } from "react-router-native";
import Auth from "./components/Auth";

import { useFonts } from "expo-font";

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  let [fontsLoaded] = useFonts({
    "Overpass-Black": require("./assets/fonts/Overpass-Black.ttf"),
    "Overpass-Light": require("./assets/fonts/Overpass-Light.ttf"),
    "Overpass-Regular": require("./assets/fonts/Overpass-Regular.ttf"),
  });

  return (
    fontsLoaded && (
      <NativeRouter>
        {/* <SafeAreaView> */}
        {/* <StatusBar
          animated={true}
          backgroundColor="#e1e5ea"
          barStyle="light-content"
          showHideTransition="slide"
        /> */}

        <View style={styles.container}>
          <Route path="/">
            {!currentUser ? (
              <Route exact path="/">
                <Auth setCurrentUser={setCurrentUser} />
              </Route>
            ) : (
              <TouchableOpacity
                style={{ backgroundColor: "#ff5", alignItems: "center" }}
                onPress={() => setCurrentUser(null)}
              >
                <Text style={{ color: "#000", padding: 10, fontSize: 22 }}>
                  LOG OUT
                </Text>
              </TouchableOpacity>
            )}
          </Route>
        </View>
        {/* </SafeAreaView> */}
      </NativeRouter>
    )
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 6,
    marginTop: 0,
  },
});

export default App;
